{
  "冥界 Netherworld": {
    "位置": "位于幻想乡之外的另一个世界",
    "性质": "人死后会去往的世界之一",
    "居民": {
      "亡灵": "形成于冥界的亡灵以及从其他地方形成最后被送往冥界的亡灵",
      "幽灵": "无罪的死者在成佛或转生前以幽灵状态逗留,也有通过其他方式形成的幽灵或来自其他地方的幽灵",
      "管理者": "西行寺幽幽子(Yuyuko Saigyouji)受阎魔委派负责管理冥界的幽灵"
    },
    "环境": {
      "四季分明": "春天开满樱花,秋天有枫叶",
      "天气变化": "会下雨、下雪",
      "生物": "鱼虫鸟兽都是死物,不会鸣叫,只能静静活动",
      "寂静": "不存在微生物,无法酿酒"
    },
    "近况": {
      "拥挤": "天界趋于饱和,逗留冥界的幽灵越积越多",
      "计划搬迁": "幽幽子曾策划将幽灵移居显界,引起注意后计划废止",
      "拓展土地": "阎魔拓展了冥界土地,如今比地狱更加宽广"
    },
    "与幻想乡的关系": {
      "幽明结界": {
        "作用": "冥界与幻想乡之间的分隔,一般活人不能去冥界",
        "春雪异变的影响": "春雪异变之后幽明结界变薄,人类幽灵可自由往来"
      }
    }
  },

  "白玉楼 Hakugyokurou": {
    "位置": "冥界中央的一座带庭院的大型宅邸",
    "所有者": "西行寺家",
    "居民": {
      "主人": "西行寺幽幽子(Yuyuko Saigyouji)",
      "园丁、护卫": "魂魄妖梦(Youmu Konpaku)"
    },
    "庭院": {
      "面积": "占地惊人",
      "樱花": "种植着难以计数的樱花树,由妖梦打理",
      "西行妖": "有一株不开花的老树,幽幽子亡骸埋于树下但不知情"
    },
    "建筑": {
      "规模": "房屋本身相当大,庭院更大",
      "风格": "整体是传统日式建筑",
      "布局": "本殿环绕枯山水中庭,呈'コ'形",
      "景观": "大厅外是铺着小卵石、栽松树的中庭,远处有屏障,越屏障是宽敞樱庭,樱庭里有大片的樱花树"
    },
    "内部": {
      "房间": "起居室、待客室、厨房等,大部分铺着榻榻米,是日式风格"
    },
    "交通": "从幻想乡去白玉楼需穿过幽明结界到冥界,走过漫长阶梯"
  },

  "西行妖 Saigyou Ayakashi": {
    "形态":"妖怪樱花树,是白玉楼庭院中最大的一棵",
    "背景故事": {
      "诗人之死": "幽幽子父亲是个才华横溢的诗人,临终时选择在最美的樱花树下长眠",
      "树木之名": "诗人死后,樱花树的美名远播,吸引许多人前来长眠",
      "妖怪化": "吸收众多亡者之血后,樱花树变成了妖怪,被称为西行妖,能主动引诱人致死",
      "幽幽子的悲剧": "那时候还是人类的幽幽子厌恶她自己的能力,在西行妖那里自杀了",
      "樱花封印": "西行妖的樱花最终被幽幽子等人的尸体封印,如今留存于白玉楼"
    }
  }
}
